<?phpclass signin extends baseController {    private $admin;    private $user;    public function __construct()    {        $this->admin = $this->model('adminm');        $this->user = $this->model('usermodel');    }    public function index(){        if(isset($_POST['submit'])){            session_start();            $user = array();            $email = $_POST['email'];            $pass = $_POST['pass'];            $datas['users'] = $this->user->findUserByEmail('users', $email);            foreach ($datas['users'] as $data) {                $user[] = $data;            }            if($user[0]['user_password'] != $pass){                $_SESSION['login_failed'] = true;                header('location:http://localhost/own_framework/');            }else{                $id = $user[0]['user_id'];                $this->user->update_user('users', $id, array(                    'user_token' => $_POST['token']                ));                $_SESSION['User_token'] = $_POST['token'];                $_SESSION['User_email'] = $_POST['email'];                // Must be changed to header to profile                header("Location: http://localhost/own_framework/profile/".$user[0]['user_id']);            }        }    }}