<?phpclass adminm extends baseModel {    public $db;    public function __construct()    {        parent::__construct();    }    public function selectAll($table){        return $this->db->select($table)->get();    }    public function findAdmins($table){        return$this->db->select($table)->get();    }    public function findAdmin($table, $email){        return$this->db->select($table, "email = '$email'")->get();    }    public function updateAdmin($table, $id, $colomns){        return $this->db->update($table, "id = $id", $colomns)->get();    }    public function selectOne($table,$id){        return $this->db->select($table, "id = $id")->get();    }    public function insert($table, $colomns){        return $this->db->insert($table, $colomns);    }    public function update($table,$id, $colomns){        return $this->db->update($table, "id = $id" , $colomns)->get();    }    public function updatemenu($table,$id, $colomns){        return $this->db->update($table, "menu_id = $id" , $colomns)->get();    }    public function remove($table, $id){        return $this->db->remove($table, "id = $id")->get();    }}