<?phpclass PageModel extends baseModel {    public $db;    public function __construct()    {        parent::__construct();    }    public function findPage($table, $page){        return $this->db->select($table, "linkname = '$page'")->get();    }    public function selectAll($table){        return $this->db->select($table)->get();    }    public function postget($table, $id){        return $this->db->select($table, "pages_id = $id")->get();    }}