<?phpclass usermodel extends baseModel {    public function __construct()    {        parent::__construct();    }    public function allUser($table){        return $this->db->select($table)->get();    }    public function findUserByEmail($table, $email){        return $this->db->select($table, "user_email = '$email'")->get();    }    public function findUserByUsername($table, $username){        return $this->db->select($table, "user_username = '$username'")->get();    }    public function findUserById($table, $id){        return $this->db->select($table, "user_id = $id")->get();    }    public function update_user($table, $id, $column){        return $this->db->update($table, "user_id = $id", $column)->get();    }}