el</h1>        <hr>        <div>            <h2>Header</h2>            <table class="table table-striped">                <thead>                <tr>                    <th>ID</th>                    <th>Img(0)/Color(1)</th>                    <th>Img source</th>                    <th>Color</th>                    <th>Height(max:350)</th>                </tr>                </thead>                <tbody>                <?php                    foreach ($header as $head){ ?>                        <tr>                            <td style="padding-top: 28px"><?=$head['id']?></td>                            <td style="padding-top: 28px">                                    <select class="form-control" name="img_or_color" id="selector" style="color: black">                                        <?php                                        if($head['img_or_color'] == 0){                                            for($i=0; $i<=1; $i++){                                                $condition = array("Image", "Color");                                                echo "<option  value='".$i."'>".$condition[$i]."</option>";                                            }                                        }else{                                            for($i=1; $i>=0; $i--){                                                $condition = array("Image", "Color");                                                echo "<option  value='".$i."'>".$condition[$i]."</option>";                                            }                                        }                                        ?>                                    </select> <br>                                <div class="img_or_color">                                </div>                            </td>                            <td style="padding-top: 28px" id="img_source">                                <form action="http://localhost/own_framework/admin/u